import os
from Crypto.Util.number import *
from secret import flag

p = getPrime(1024)
q = getPrime(1024)
n = p * q
e1 = 2
e2 = 3

assert len(flag) == 30

m1 = bytes_to_long(flag[:len(flag)//2])
m2 = bytes_to_long(flag[len(flag)//2:] + os.urandom(71))
assert m2 < n

c1 = pow(m1, e1, n)
c2 = pow(m2, e2, n)

print(f'c1 = {c1}')
print(f'c2 = {c2}')
print(f'n = {n}')

