from Crypto.Util.number import *
from secret import flag, e1, e2

p = getPrime(512)
q = getPrime(512)
n = p * q
ee = e1 * e2
m = bytes_to_long(flag)

assert len(flag) == 27

c1 = pow(m, e1, n)
c2 = pow(m, e2, n)

print(f'e1*e2 = {ee}')
print(f'n = {n}')
print(f'c1 = {c1}')
print(f'c2 = {c2}')
